mapper.update:
update bbs 
set bbsTitle = #{bbsTitle},
	bbsContent = #{bbsContent},
	bbsOC = #{bbsOC}			
where BBSNO = #{bbsNo}

mapper.select:
select * from ( select rownum as rn, a.* from (select * from
(select * from bbs where bbsCate = 'health')
order by bbsNo desc) a ) where rn > (#{pageNum}-1) * #{amount} and rn <= #{pageNum} * #{amount}

